// 40/100 lol

int cal(int x) {
	int ans=0;
	while (x > 1) {
		if (x%3 == 0) {
			int X=x;
			x /= 3;
			ans += min(3, X-x);
		}
		else x--, ans++;
	}
	return ans;
}

